    onRadiosChange = function() {
      updatePlaceholder();

      setTimeout(function(){
        textarea.focus();
      }, firstTime ? 500 : 0);

      if (firstTime) {
        firstTime = false;
      }
    };

    Array.prototype.forEach.call(radios, function(radio){
      radio.addEventListener('input', onRadiosChange);
